import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import introImage from '../../assets/image-top.png';

export default function Intro() {
  return (
    <Warpper>
      <Date>2024 04 28</Date>
      <IntroImg src={introImage} />
      <IntroNameBox>
        <SubText>신랑</SubText>
        <Name>이상성</Name>
        <Symbol>・</Symbol>
        <SubText>신부</SubText>
        <Name>류경민</Name>
      </IntroNameBox>
      <IntroTextBox></IntroTextBox>
    </Warpper>
  );
}

const Warpper = styled.div`
  margin-top: 6rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Date = styled.h1`
  color: var(--color-main);
  width: 8rem;
  height: 3rem;
  border: 1px solid;
  border-radius: 100%;
  line-height: 3rem;
  text-align: center;
`;

const IntroImg = styled.img`
  margin-top: 3rem;
  width: 320px;
`;

const IntroNameBox = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 2rem;
`;

const SubText = styled.p`
  font-size: 0.8rem;
  color: #515151;
`;

const Name = styled.p``;

const Symbol = styled.p`
  margin: 0 5px;
`;

const IntroTextBox = styled.div``;
