import React, { useState } from 'react';
import styled from 'styled-components';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import arrowGreen from '../../assets/icon-arrow-green.png';
import arrowBeige from '../../assets/icon-arrow-beige.png';
import kakaoPay from '../../assets/kakaopay.png';

export default function DropDown({ modifier, name, account }) {
  const [isOpen, setIsOpen] = useState(false);

  const DropDown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <DropContainer>
      <GroomtCover onClick={DropDown}>
        <GroomText>신랑측</GroomText>
      </GroomtCover>
      {isOpen && (
        <ul>
          <li>
            <NameCardContainer>
              <Modifier>{modifier}</Modifier>
              <Name>{name}</Name>
              <a
                href=" https://qr.kakaopay.com/FEi3Gomyq "
                target="_blank"
                rel="noreferrer"
              >
                <PayButton src={kakaoPay} alt="카카오페이">
                  카카오페이
                </PayButton>
                <CopyContainer>
                  <Account>{account}</Account>
                  <CopyToClipboard
                    text="서울 강남구 도곡로99길 16"
                    onCopy={handleCopyClick}
                  >
                    <CopyButton>주소 복사하기</CopyButton>
                  </CopyToClipboard>
                </CopyContainer>
              </a>
            </NameCardContainer>
          </li>
        </ul>
      )}
      <BridaltCover onClick={DropDown}>
        <BridalText>신부측</BridalText>
      </BridaltCover>
    </DropContainer>
  );
}

export const DropContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 3rem;
`;

export const GroomtCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #e6e7dd;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowGreen}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;

export const GroomText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #595d3b;
`;

export const BridaltCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #efeae2;
  margin-top: 0.5rem;
`;

export const BridalText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #856442;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowBeige}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;

export const NameCardContainer = styled.div``;

export const Modifier = styled.span``;

export const Name = styled.span``;

export const Account = styled.span``;

export const PayButton = styled.button``;
