import React, { useState } from 'react';
import styled from 'styled-components';
import { HeadText } from './Calender';
import { imgData } from '../assets/imgData';
import { SlArrowLeft, SlArrowRight } from 'react-icons/sl';

export default function Gallery() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const nextSlide = () => {
    setCurrentSlide(currentSlide === imgData.length - 1 ? 0 : currentSlide + 1);
  };
  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? imgData.length - 1 : currentSlide - 1);
  };
  return (
    <div>
      <HeadText>갤러리</HeadText>
      <GalleryWrapper>
        <ImgContainer>
          {imgData.map((img, index) => (
            <GalleryImg
              key={index}
              src={img.src}
              alt={img.alt}
              // className={currentSlide === index ? '' : 'slide-hidden'}
            />
          ))}
        </ImgContainer>
        <SlArrowLeft onClick={prevSlide} className="arrow arrow-left" />
        <SlArrowRight onClick={nextSlide} className="arrow arrow-right" />
      </GalleryWrapper>
    </div>
  );
}

export const GalleryWrapper = styled.div`
  position: relative;

  .arrow {
    position: absolute;
    width: 2rem;
    height: 2rem;
    color: white;
    filter: drop-shadow(0px 0px 3px #888);
  }

  .arrow-left {
    left: 1rem;
  }

  .arrow-right {
    right: 1rem;
  }

  .arrow:hover {
    cursor: pointer;
  }

  .slide-hidden {
    display: none;
  }
`;

export const ImgContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
`;

export const GalleryImg = styled.img`
  max-width: 100%;
  height: auto;
  object-fit: contain;
`;

export const ArrowLeftButton = styled.button``;
export const ArrowRightButton = styled.button``;
