import React, { useState } from 'react';
import styled from 'styled-components';
import galleryBg from '../assets/gallery-bg.jpg';
import { imgData } from '../assets/imgData';

export default function Gallery() {
  const [touchStartX, setTouchStartX] = useState(0);
  const [touchEndX, setTouchEndX] = useState(0);
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleTouchStart = (e) => {
    setTouchStartX(e.touches[0].clientX);
  };

  const handleTouchMove = (e) => {
    setTouchEndX(e.touches[0].clientX);
  };

  const handleTouchEnd = () => {
    const difference = touchStartX - touchEndX;

    if (difference > 150) {
      // Swipe left, go to next slide
      setCurrentSlide((prevSlide) =>
        prevSlide === imgData.length - 1 ? 0 : prevSlide + 1
      );
    } else if (difference < -150) {
      // Swipe right, go to previous slide
      setCurrentSlide((prevSlide) =>
        prevSlide === 0 ? imgData.length - 1 : prevSlide - 1
      );
    }
  };

  return (
    <div>
      <GalleryWrapper>
        <ImgContainer
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          {imgData.map((img, index) => (
            <GalleryImg
              key={index}
              src={img.src}
              alt={img.alt}
              isVisible={currentSlide === index}
              variSize={img.width > img.height}
            />
          ))}
        </ImgContainer>
        <IndicatorWrapper>
          {imgData.map((_, index) => (
            <IndicatorButton
              key={index}
              onClick={() => setCurrentSlide(index)}
              isCurrentSlide={currentSlide === index}
            />
          ))}
        </IndicatorWrapper>
      </GalleryWrapper>
    </div>
  );
}

export const GalleryWrapper = styled.div`
  width: 100%;
  padding-bottom: 3rem;
  background: url(${galleryBg});
  background-size: 100% auto;
`;

export const ImgContainer = styled.div`
  cursor: pointer;
  max-width: 400px;
  height: 600px;
  margin: 0 auto;
  padding: 1rem;
  display: flex;
  align-items: center;
  transition: transform 1s ease-in-out;
`;

export const GalleryImg = styled.img`
  width: 100%;
  max-height: 500px;
  object-fit: ${(props) => (props.variSize ? 'contain' : 'cover')};
  display: ${(props) => (props.isVisible ? 'inline-block' : 'none')};
`;

export const IndicatorWrapper = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const IndicatorButton = styled.button`
  cursor: pointer;
  width: 5px;
  height: 5px;
  border-radius: 50%;
  border: none;
  outline: none;
  margin: 0 0.2rem;
  background-color: ${(props) => (props.isCurrentSlide ? '#000' : '#afafaf;')};
`;
