import React, { useState } from 'react';
import styled from 'styled-components';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Contact from '../../sections/Contact';
import { groomData, brideData } from './accountData'; // 데이터 파일 경로에 맞게 조정해야 합니다.

export default function DropDown() {
  const [isOpen, setIsOpen] = useState({ groom: false, bride: false });

  const handleToggle = (section) => {
    setIsOpen((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  return (
    <DropContainer>
      <ContactWrapper>
        <HeadText>마음 전하실 곳</HeadText>
        <Hr />
        <NoticeContainer>
          <NoticeText>
            저희의 소중한 시작을 <br />
            함께해 주시는 모든 분들께 감사드립니다.
          </NoticeText>
        </NoticeContainer>
      </ContactWrapper>
      <StyledToastContainer />
      <Contact
        title="신랑측"
        data={groomData}
        isOpen={isOpen.groom}
        onToggle={() => handleToggle('groom')}
      />
      <Contact
        title="신부측"
        data={brideData}
        isOpen={isOpen.bride}
        onToggle={() => handleToggle('bride')}
      />
    </DropContainer>
  );
}

export const StyledToastContainer = styled(ToastContainer)`
  .Toastify__toast {
    font-size: 1rem;
    border-radius: 0.5rem;
    padding: 1rem 1.5rem;
    color: #fff;
    background: rgba(0, 0, 0, 1);
    bottom: 1rem;
  }
`;

export const DropContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 3rem;
`;
