import React from 'react';
import styled from 'styled-components';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { HeadText, NoticeContainer, NoticeText } from './WayToCome';
import { Hr } from './Calender';
import DropDown from '../components/contact/DropDown';

export default function Contact() {
  const TOAST = {
    position: toast.POSITION.BOTTOM_CENTER,
    autoClose: 1000,
    closeButton: false,
    icon: true,
    hideProgressBar: true,
    progress: undefined,
  };

  const handleCopyClick = (isCopySuccess) => {
    if (isCopySuccess) {
      toast.success('계좌가 복사되었습니다!', TOAST);
    } else {
      toast.error('계좌 복사에 실패했습니다.', TOAST);
    }
  };

  return (
    <>
      <ContactWrapper>
        <StyledToastContainer />
        <HeadText>마음 전하실 곳</HeadText>
        <Hr />
        <NoticeContainer>
          <NoticeText>
            참석이 어려우신 분들은 <br />
            축하의 마음을 전해주세요.
          </NoticeText>
        </NoticeContainer>
        <DropDown
          modifier="어머니"
          name="권남행"
          account="국민은행 1234-567-123485"
        />
        <AccountCopyContainer>
          <CopyToClipboard
            text="서울 강남구 도곡로99길 16"
            onCopy={handleCopyClick}
          >
            <AccountCopyButton>복사</AccountCopyButton>
          </CopyToClipboard>
        </AccountCopyContainer>
      </ContactWrapper>
    </>
  );
}
export const StyledToastContainer = styled(ToastContainer)`
  .Toastify__toast {
    font-size: 1rem;
    border-radius: 0.5rem;
    padding: 1rem 1.5rem;
    color: #fff;
    background: rgba(0, 0, 0, 1);
    bottom: 1rem;
  }
`;

export const ContactWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  background-color: #fefbf8;
  padding: 4rem 2rem;
`;

export const AccountCopyContainer = styled.div``;

export const AccountCopyButton = styled.button``;
