import React from 'react';
import styled from 'styled-components';

export default function HostInfo({ subText, hostName, groom, bride }) {
  return (
    <>
      <HostInfoTag>
        <SubText>{subText}</SubText>
        <Name>{hostName}</Name>
      </HostInfoTag>
      <MainNameText>
        {groom} <br /> & <br />
        {bride}
      </MainNameText>
      <InfoDesc>결혼합니다.</InfoDesc>
    </>
  );
}

export const HostInfoTag = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

export const SubText = styled.p`
  font-size: 0.8rem;
  color: #515151;
`;

export const Name = styled.p`
  font-size: 1rem;
`;

export const MainNameText = styled.h1`
  text-align: center;
  font-size: 1.3rem;
  line-height: 2.5rem;
`;

export const InfoDesc = styled.p`
  margin-top: 6rem;
  font-size: 1.3rem;
`;
