import React, { useState } from 'react';
import styled from 'styled-components';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Hr } from '../../sections/Calender';
import arrowGreen from '../../assets/icon-arrow-green.png';
import arrowBeige from '../../assets/icon-arrow-beige.png';
import kakaoPay from '../../assets/kakaopay.png';

export default function DropDown({ modifier, name, account }) {
  const [isOpen, setIsOpen] = useState(false);

  const DropDown = () => {
    setIsOpen(!isOpen);
  };

  const TOAST = {
    position: toast.POSITION.BOTTOM_CENTER,
    autoClose: 1000,
    closeButton: false,
    icon: true,
    hideProgressBar: true,
    progress: undefined,
  };

  const handleCopyClick = (isCopySuccess) => {
    if (isCopySuccess) {
      toast.success('계좌가 복사되었습니다!', TOAST);
    } else {
      toast.error('계좌 복사에 실패했습니다.', TOAST);
    }
  };

  return (
    <DropContainer>
      <StyledToastContainer />
      <GroomtCover onClick={DropDown}>
        <GroomText>신랑측</GroomText>
      </GroomtCover>
      {isOpen && (
        <NameCardContainer>
          <ul>
            {data.map((item, index) => (
              <li key={index}>
                <AccountInfo>
                  <Modifier>{item.modifier}</Modifier>
                  <Name>{item.name}</Name>
                  <a href={item.payUrl} target="_blank" rel="noreferrer">
                    <PayButton></PayButton>
                  </a>
                </AccountInfo>
                <AccountInfo>
                  <AccountCopyContainer>
                    <Account>{account}</Account>
                    <CopyToClipboard text={account} onCopy={handleCopyClick}>
                      <AccountCopyButton>복사</AccountCopyButton>
                    </CopyToClipboard>
                  </AccountCopyContainer>
                </AccountInfo>
                <Hr />
              </li>
            ))}
          </ul>
        </NameCardContainer>
      )}
      <BridaltCover onClick={DropDown}>
        <BridalText>신부측</BridalText>
      </BridaltCover>
    </DropContainer>
  );
}

export const StyledToastContainer = styled(ToastContainer)`
  .Toastify__toast {
    font-size: 1rem;
    border-radius: 0.5rem;
    padding: 1rem 1.5rem;
    color: #fff;
    background: rgba(0, 0, 0, 1);
    bottom: 1rem;
  }
`;

export const DropContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 3rem;
`;

export const GroomtCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #e6e7dd;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowGreen}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;

export const GroomText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #595d3b;
`;

export const BridaltCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #efeae2;
  margin-top: 0.5rem;
`;

export const BridalText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #856442;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowBeige}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;

export const NameCardContainer = styled.div`
  width: 100%;
  background-color: #fff;
  padding: 1.3rem;
`;

export const AccountInfo = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: -0.8rem;
`;

export const Modifier = styled.p`
  font-size: 0.7rem;
  margin-right: 0.2rem;
`;

export const Name = styled.h3`
  font-size: 0.9rem;
`;

export const Account = styled.span`
  font-size: 0.9rem;
`;

export const PayButton = styled.button`
  width: 4rem;
  height: 2rem;
  background: url(${kakaoPay}) no-repeat center center;
  background-size: contain;
  border-radius: 0.6rem;
`;

export const AccountCopyContainer = styled.div``;

export const AccountCopyButton = styled.button`
  width: 4rem;
  height: 2rem;
  border: 1px solid #b3b3b3;
  border-radius: 0.6rem;
  background: none;
  font-size: 0.9rem;
  color: #8d8d8d;
`;
