import React, { useState } from 'react';
import styled from 'styled-components';
import { imgData } from '../assets/imgData';
import { SlArrowLeft, SlArrowRight } from 'react-icons/sl';

export default function Gallery() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const nextSlide = () => {
    setCurrentSlide(currentSlide === imgData.length - 1 ? 0 : currentSlide + 1);
  };
  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? imgData.length - 1 : currentSlide - 1);
  };
  return (
    <div>
      <GalleryWrapper>
        <ImgContainer>
          {imgData.map((img, index) => (
            <GalleryImg
              key={index}
              src={img.src}
              alt={img.alt}
              className={currentSlide === index ? '' : 'slide-hidden'}
              isLandscape={img.width > img.height}
            />
          ))}
        </ImgContainer>
        <SlArrowLeft onClick={prevSlide} className="arrow arrow-left" />
        <SlArrowRight onClick={nextSlide} className="arrow arrow-right" />
      </GalleryWrapper>
    </div>
  );
}

export const GalleryWrapper = styled.div`
  max-width: 520px;
  overflow: hidden;
  text-align: center;

  .arrow {
    width: 2rem;
    height: 2rem;
    color: white;
    filter: drop-shadow(0px 0px 3px #888);
  }

  .arrow-left {
    left: 1rem;
    bottom: 10%;
  }

  .arrow-right {
    right: 1rem;
    top: 50%;
  }

  .arrow:hover {
    cursor: pointer;
  }

  .slide-hidden {
    display: none;
  }
`;

export const ImgContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 700px;
  overflow: hidden;
`;

export const GalleryImg = styled.img`
  width: 520px;
  height: auto;
  max-width: 100%;
  max-height: 100%;
  object-fit: ${(props) => (props.isLandscape ? 'contain' : 'cover')};
`;
