import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import introImage from '../../assets/image-top.png';

export default function Intro() {
  return (
    <Warpper>
      <Date>2024 04 28</Date>
      <IntroImg src={introImage} />
      <IntroTextBox>
        <SubText>신랑</SubText>
        <Name>이상성</Name>
        <Symbol>・</Symbol>
        <SubText>신부</SubText>
        <Name>류경민</Name>
      </IntroTextBox>
      <MainTextBox>
        <MainText>상성</MainText>
        <MainText>&</MainText>
        <MainText>경민</MainText>
        <MainBottomText>결혼합니다.</MainBottomText>
      </MainTextBox>
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width="current"
        height="current"
        viewBox="0 0 860 240"
        fill="none"
      >
        <path
          d="M-114 140.773C-87.0613 190.983 -16.6849 211.054 2.33328 217.202C74.0835 240.397 157.805 239.179 233.167 236.758C301.929 234.549 381.168 230.166 429.057 181.679C457.924 152.452 484.971 117.309 488.666 77.7995C490.346 59.8435 485.984 -13.0243 446.323 4.77897C431.224 11.5566 417.853 33.0226 412.5 46C408.254 56.2935 418 55 418.779 49.9907C404.843 37.1108 386.495 29.2951 366.98 24.8731C343.841 19.6296 334.437 17.9503 326.487 37.6113C311.797 73.941 339.993 115.262 367.597 140.773C419.677 188.904 481.907 220.176 559.376 213.973C618.344 209.251 778.416 217.202 877 77.7995"
          stroke="#F56700"
          stroke-opacity="0.3"
          stroke-width="3"
          stroke-linecap="round"
        />
      </svg>
    </Warpper>
  );
}

const Warpper = styled.div`
  margin-top: 6rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Date = styled.p`
  color: var(--color-main);
  width: 8rem;
  height: 3rem;
  border: 1px solid;
  border-radius: 100%;
  line-height: 3rem;
  text-align: center;
`;

const IntroImg = styled.img`
  width: 100%;
  height: auto;
  margin-top: 3rem;
  max-width: 320px;

  @media (max-width: 300px) {
    max-width: 240px;
  }
`;

const IntroTextBox = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 2rem;
`;

const SubText = styled.p`
  font-size: 0.8rem;
  color: #515151;
`;

const Name = styled.p``;

const Symbol = styled.p`
  margin: 0 5px;
`;

const MainTextBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 6px;
  margin-top: 5rem;
`;

const MainText = styled.h1`
  font-size: 1.2rem;
`;

const MainBottomText = styled.p`
  margin-top: 2rem;
  font-size: 1.2rem;
`;
