import React from 'react';
import styled from 'styled-components';
import introImage from '../../assets/image-top.png';
import heart from '../../assets/heart.svg';

export default function Intro() {
  return (
    <div>
      <Warpper>
        <Date>2024 04 28</Date>
        <IntroImg src={introImage} />
        <TextBox>
          <SubText>신랑</SubText>
          <Name>이상성</Name>
          <Symbol>・</Symbol>
          <SubText>신부</SubText>
          <Name>류경민</Name>
        </TextBox>
        <Heart src={heart} />
      </Warpper>
    </div>
  );
}

const Warpper = styled.div`
  margin-top: 6rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Date = styled.h1`
  color: var(--color-main);
  width: 8rem;
  height: 3rem;
  border: 1px solid;
  border-radius: 100%;
  line-height: 3rem;
  text-align: center;
`;

const IntroImg = styled.img`
  margin-top: 3rem;
  width: 320px;
`;

const TextBox = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 2rem;
`;

const SubText = styled.p`
  font-size: 0.8rem;
  color: #515151;
`;

const Name = styled.p``;

const Symbol = styled.p`
  margin: 0 5px;
`;

const Heart = styled.svg`
  stroke-width: 3px;
  fill: rgba(245, 103, 0, 0.3);
`;
