import React from 'react';
import styled from 'styled-components';
import arrowGreen from '../../assets/icon-arrow-green.png';
import arrowBeige from '../../assets/icon-arrow-beige.png';

export default function DropList() {
  return (
    <DropContainer>
      <GroomtCover>
        <GroomText>신랑측</GroomText>
      </GroomtCover>
      <BridaltCover>
        <BridalText>신부측</BridalText>
      </BridaltCover>
    </DropContainer>
  );
}
export const DropContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 3rem;
`;

export const GroomtCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #e6e7dd;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowGreen}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;

export const GroomText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #595d3b;
`;

export const BridaltCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #efeae2;
  margin-top: 0.5rem;
`;

export const BridalText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #856442;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowBeige}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;
