import React from 'react';
import styled from 'styled-components';
import { HeadText, NoticeContainer, NoticeText } from './WayToCome';
import { Hr } from './Calender';
import arrowGreen from '../assets/icon-arrow-green.png';
import arrowBeige from '../assets/icon-arrow-beige.png';

export default function Contact() {
  return (
    <>
      <ContactWrapper>
        <HeadText>마음 전하실 곳</HeadText>
        <Hr />
        <NoticeContainer>
          <NoticeText>
            참석이 어려우신 분들은 <br />
            축하의 마음을 전해주세요.
          </NoticeText>
        </NoticeContainer>
        <DropContainer>
          <GroomtCover>
            <GroomText>신랑측</GroomText>
          </GroomtCover>
          <BridaltCover>
            <BridalText>신부측</BridalText>
          </BridaltCover>
        </DropContainer>
      </ContactWrapper>
    </>
  );
}

export const ContactWrapper = styled.div`
  width: 100%;
  background-color: #fefbf8;
  padding: 4rem 2rem;
`;

export const DropContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 4rem;
`;

export const GroomtCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #e6e7dd;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowGreen}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;

export const GroomText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #595d3b;
`;

export const BridaltCover = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  width: 20rem;
  height: 3.5rem;
  background-color: #efeae2;
  margin-top: 0.5rem;
`;

export const BridalText = styled.h3`
  margin-left: 1rem;
  font-size: 0.9rem;
  color: #856442;

  &::after {
    content: '';
    position: absolute;
    background: url(${arrowBeige}) no-repeat center center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    right: 1rem;
  }
`;
