import React from 'react';
import styled from 'styled-components';
import introImage from '../../assets/image-top.png';
import Heart from '../../components/common/Heart';

export default function Intro() {
  return (
    <div className="fadeinup">
      <Warpper>
        <Date>2024 04 28</Date>
        <IntroImg src={introImage} />
        <IntroTextBox>
          <SubText>신랑</SubText>
          <Name>이상성</Name>
          <Symbol>・</Symbol>
          <SubText>신부</SubText>
          <Name>류경민</Name>
        </IntroTextBox>
        <MainTextBox>
          <MainTitText>상성</MainTitText>
          <MainTitText>&</MainTitText>
          <MainTitText>경민</MainTitText>
          <MainBottomText>결혼합니다.</MainBottomText>
        </MainTextBox>
        <Heart />
      </Warpper>
    </div>
  );
}

const Warpper = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 6rem;
`;

const Date = styled.p`
  color: var(--color-main);
  width: 8rem;
  height: 3rem;
  border: 1px solid;
  border-radius: 100%;
  line-height: 3rem;
  text-align: center;
`;

const IntroImg = styled.img`
  width: 100%;
  height: auto;
  margin-top: 3rem;
  max-width: 320px;

  @media (max-width: 300px) {
    max-width: 240px;
  }
`;

const IntroTextBox = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-top: 2rem;
`;

const SubText = styled.p`
  font-size: 0.8rem;
  color: #515151;
`;

const Name = styled.p``;

const Symbol = styled.p`
  margin: 0 5px;
`;

const MainTextBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  margin-top: 5rem;
`;

const MainTitText = styled.h1`
  font-size: 1.2rem;
`;

const MainBottomText = styled.p`
  margin-top: 4rem;
  font-size: 1.2rem;
`;
